document.onreadystatechange=function(){"complete"!==document.readyState?(document.querySelector("body").style.visibility="hidden",document.querySelector("#loader").style.visibility="visible"):(document.querySelector("#loader").style.display="none",document.querySelector("body").style.visibility="visible")};let tlPageLoad=anime.timeline({});tlPageLoad.add({targets:"nav",easing:"easeOutExpo",translateY:[-200,0],duration:2e3},0),tlPageLoad.add({targets:".logo-wrapper",easing:"easeOutExpo",opacity:1,duration:100},0),tlPageLoad.add({targets:".text-wrapper, .imgdesc-wrapper, .print, iframe",easing:"easeOutExpo",translateY:[100,0],opacity:[0,1],duration:1200},100);let closeIcon=document.querySelector(".close"),logoWrapper=document.querySelector(".logo-wrapper");logoWrapper.addEventListener("mouseenter",e=>{anime.remove(closeIcon),anime({easing:"easeInOutExpo",targets:closeIcon,rotateZ:"90deg",duration:300})}),logoWrapper.addEventListener("mouseleave",e=>{anime.remove(closeIcon),anime({easing:"easeInOutExpo",targets:closeIcon,rotateZ:"0deg",duration:300})});